import { useState, useEffect } from "react";
import { useNavigate, Link, useLocation } from "react-router-dom";
import API, { apiCall } from "../api/api";

export default function AdminDashboard() {
  const [activeTab, setActiveTab] = useState("overview");
  const [stats, setStats] = useState({
    totalUsers: 0,
    farmers: 0,
    buyers: 0,
    sellers: 0,
    deliveryPartners: 0,
    pendingVerifications: 0,
    totalOrders: 0,
    totalRevenue: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Set active tab based on current path
    const path = location.pathname;
    if (path === "/admin/dashboard") {
      setActiveTab("overview");
    } else if (path === "/admin/production-verification") {
      setActiveTab("production");
    } else if (path === "/admin/delivery-verification") {
      setActiveTab("delivery");
    } else if (path === "/admin/user-management") {
      setActiveTab("users");
    } else if (path === "/admin/order-management") {
      setActiveTab("orders");
    } else if (path === "/admin/analytics") {
      setActiveTab("analytics");
    }

    // Check if admin is logged in
    const adminToken = localStorage.getItem("adminToken");
    if (!adminToken) {
      navigate("/admin/login");
      return;
    }

    fetchDashboardData();
  }, [navigate, location.pathname]);

  const fetchDashboardData = async () => {
    try {
      const { data } = await apiCall(() => API.get("/admin/dashboard-stats"));
      if (data) {
        setStats(data);
      }
    } catch (error) {
      console.error("Error fetching admin dashboard data:", error);
      setError("Failed to fetch dashboard data");
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("adminToken");
    localStorage.removeItem("adminData");
    navigate("/admin/login");
  };

  if (loading) {
    return (
      <div className="container" style={{ paddingTop: "40px", paddingBottom: "40px" }}>
        <div style={{ textAlign: "center" }}>
          <div className="loading-spinner"></div>
          <p>Loading admin dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container" style={{ paddingTop: "40px", paddingBottom: "40px" }}>
      <div className="page-header" style={{ marginBottom: "32px" }}>
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <div>
            <h1>üõ°Ô∏è Admin Dashboard</h1>
            <p>KisanSetu Administration Portal</p>
          </div>
          <div style={{ display: "flex", gap: "12px", alignItems: "center" }}>
            <span style={{ color: "var(--text-secondary)" }}>
              Welcome, Admin
            </span>
            <button
              onClick={handleLogout}
              className="btn btn-secondary"
              style={{ fontSize: "14px", padding: "8px 16px" }}
            >
              üö™ Logout
            </button>
          </div>
        </div>
      </div>

      {error && <div className="error-message" style={{ marginBottom: "16px" }}>{error}</div>}

      {/* Tab Navigation */}
